    def showclasscification(self):
        list = ["正常音","湿罗音","干啰音"]
        i = random.randint(0,2)
        ill = list[i]
        self.result.setText(ill)


    def DisplayPatient(self):
        result = model.Database().SearchInfo('select * from patientinfo')[-1]
        self.name.setText(result[1])
        self.sex.setText(result[2])
        self.year.setText(result[3])
        self.weight.setText(result[4])
        self.illness.setText(result[5])



    def getfile(self):  # 获取文件路径
        file = QFileDialog()
        file.setDirectory('C:\\')
        if file.exec_():
            filename = file.selectedFiles()[0]
            self.lineEdit.setText(filename)

    def get_start_end(self):
        self.start = self.doubleSpinBox.value()
        self.end = self.doubleSpinBox_2.value()

    def GetBreatheData(self):  # 提取呼吸音数据
        filename = self.lineEdit.text()
        f = wave.open(filename, "rb")
        # (nchannels, sampwidth, framerate, nframes, comptype, compname)
        params = f.getparams()
        nchannels, sampwidth, framerate, nframes = params[:4]
        str_data = f.readframes(nframes)
        f.close()
        self.wave_data = np.fromstring(str_data, dtype=np.short)
        if nchannels == 2:
            self.wave_data.shape = -1, 2
            self.wave_data = self.wave_data.T
            self.time = np.arange(0, nframes) * (1.0 / framerate)
        elif nchannels == 1:
            self.wave_data.shape = -1, 1
            self.wave_data = self.wave_data.T
            self.time = np.arange(0, nframes) * (1.0 / framerate)

    def Graph(self):
        start = list(self.time).index(self.start)
        end = list(self.time).index(self.end)
        self.y_array = self.wave_data[0][start:end]
        self.textBrowser.setText(str(self.y_array))
        self.widget2.plot(self.y_array, name="mode1")

    def savedata(self):
        print("保存数据被点击")
        self.data = list(self.y_array)
        pd_data = pd.DataFrame(data=self.data)
        pd_data.to_csv('data.csv', index = False)

